<template>    <v-container grid-list-xl text-xs-center>        <v-layout class="center-login" row wrap>            <v-flex xs10 offset-xs1>                <img src="http://ksol.fr/img/portfolio/scalingo.png" alt="scalingo-logo"/>            </v-flex>            <v-content >                <v-container fluid fill-height >                    <v-layout                            justify-center                            align-center                    >                        <v-flex                                text-xs-center                                xs12                                md8                        >                            <v-form                                    ref="form"                                    lazy-validation                            >                                <v-card class="card-login elevation-20" height='100%'>                                    <p>In order to login, you must provide a token that you can find <a                                            href="https://my.scalingo.com/profile" target="_blank">here</a></p>                                    <v-text-field                                        label="Enter your token."                                        v-model="token"                                    >                                    </v-text-field>                                    <v-btn                                        :disabled="isDisabled"                                        @click="submit"                                    >                                        Submit                                    </v-btn>                                </v-card>                            </v-form>                        </v-flex>                    </v-layout>                </v-container>            </v-content>        </v-layout>    </v-container></template><script>    import axios from "axios";    import store from "../store";    export default {        name: "Login",        data: function (){            return {                token: "",                isDisabled: true,                error: false,            }        },        methods: {            submit() {                axios({                    method: "post",                    url: "https://auth.scalingo.com/v1/tokens/exchange",                    auth: {                        username: '',                        password: this.token                    }                }).then(response => {                    store.dispatch('setToken', response.data.token);                    this.$router.push(this.$route.query.redirect || '/');                }).catch(e => {                    this.error = true;                })            },        },        created() {            this.$multiwatch(['token'], function() {                this.isDisabled = this.token === "";            });            if (store.state.token)                this.$router.push(this.$route.query.redirect || '/');        }    }</script><style scoped>    .error-msg {        color: red;        font-weight: bold;    }    .center-login {        margin-top: 10%;        margin-left: 30%;        margin-right: auto;    }    img {        width: 60%;    }    .card-login {        padding: 2em;    }</style>