<template>    <v-container fill-height>        <v-layout wrap >            <v-data-table                :headers="headers"                :items="appList"                class="card-apps"            >                <template slot="items" slot-scope="props">                    <td >{{ props.item.name }}</td>                    <td class="text-xs-left">{{ props.item.id }}</td>                    <td class="text-xs-left">{{ props.item.status }}</td>                    <td class="text-xs-left">{{ props.item.created_at }}</td>                    <td class="text-xs-left">{{ props.item.owner.username }}</td>                    <td @click="loadDomains(props.item.name)">                        <v-btn flat icon>                            <v-icon>build</v-icon>                        </v-btn>                    </td>                </template>            </v-data-table>        </v-layout>    </v-container></template><script>    import mainApi from "../api/mainApi";    export default {        name: "Apps",        data: function () {            return {                appList: [],                dialog: false,                domains: null,                headers: [                    {                        text: 'Applications',                        align: 'left',                        value: 'name'                    },                    { text: 'Id', value: 'Id'},                    { text: 'Status', value: 'Status' },                    { text: 'Creation', value: 'Creation' },                    { text: 'Owner', value: 'Owner' },                    { text: 'Settings', value: 'Settings' }                ],            }        },        methods: {            loadDomains(appName) {                this.$router.push(this.$route.query.redirect || appName);            }        },        created() {            mainApi().get('apps', {})                .then(response => {                    this.appList = response.data.apps;                }).catch(e => {                    throw e;            });        }    }</script><style scoped>    .card-apps {        padding-top: 7em;        height: 100%;        width: 100%;        margin-right: auto;    }</style>